import "../App.css";

function CommentBox() {
  return (
    <form className="comment-box">
      <label>Name</label>
      <input type="text" name="name" className="form-input" />
      <input type="textarea" name="comment" className="form-input textarea" rows="10"/>
      <input type="submit" value="Comment" className="form-input" />
    </form>
  );
}

export default CommentBox;
